import {
  ImageBackground,
  StyleSheet,
  Image,
  ScrollView,
  TouchableOpacity,
  FlatList,
} from "react-native";
import { Text, View } from "@/components/Themed";
import { LinearGradient } from "expo-linear-gradient";
import Constants from "expo-constants";
import { AntDesign } from "@expo/vector-icons";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "expo-router";

export default function TabOneScreen() {
  const [data1, setData1] = useState<any>([]);
  const [data2, setData2] = useState<any>([]);
  useEffect(() => {
    const fetchData1 = async () => {
      try {
        const result = await axios.get(
          "https://kitsu.io/api/edge/anime?sort=popularityRank"
        );
        setData1(result.data.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData1();
    const fetchData2 = async () => {
      try {
        const result = await axios.get(
          "https://kitsu.io/api/edge/anime?filter[status]=upcoming"
        );
        setData2(result.data.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData2();
  }, []);

  return (
    <View style={styles.container}>
      <ScrollView style={{ position: "relative" }}>
        <ImageBackground
          source={{
            uri: "data:image/jpeg;base64,",
          }}
          resizeMode="cover"
          style={{
            width: 400,
            height: 370,
            flex: 1,
            justifyContent: "flex-end",
          }}
        >
          <LinearGradient
            colors={["transparent", "rgba(0,0,0,1)"]}
            style={{
              position: "absolute",
              width: "100%",
              height: "50%",
              flex: 1,
            }}
          />
          <View
            style={{
              flex: 1,
              justifyContent: "flex-end",
              marginBottom: 30,
              backgroundColor: "",
              padding: 20,
            }}
          >
            <Text
              style={{
                color: "white",
                fontSize: 22,
                fontWeight: "bold",
              }}
            >
              Demon Slayer: Kimetsu ...
            </Text>
            <Text style={{ fontSize: 12 }}>
              Action, Adventure, Shounen, Martial Arts, ...
            </Text>
            <View
              style={{
                backgroundColor: "transparent",
                width: 180,
                flexDirection: "row",
                justifyContent: "space-between",
              }}
            >
              <TouchableOpacity
                style={{
                  flex: 1,
                }}
              >
                <Text
                  style={{
                    backgroundColor: "#06C149",
                    borderRadius: 30,
                    height: 40,
                    width: 80,
                    padding: 10,
                    textAlign: "center",
                    fontWeight: "bold",
                  }}
                >
                  Play
                </Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={{
                  flex: 1,
                }}
              >
                <Text
                  style={{
                    borderWidth: 3,
                    borderColor: "white",
                    borderRadius: 30,
                    height: 40,
                    width: 100,
                    padding: 10,
                    textAlign: "center",
                    fontWeight: "bold",
                  }}
                >
                  + My List
                </Text>
              </TouchableOpacity>
            </View>
          </View>
        </ImageBackground>

        <View style={{ paddingLeft: 20 }}>
          <View style={{ paddingBottom: 20 }}>
            <View
              style={{
                flex: 1,
                flexDirection: "row",
                justifyContent: "space-between",
                paddingRight: 20,
              }}
            >
              <Text
                style={{ fontWeight: "bold", fontSize: 20, marginBottom: 20 }}
              >
                Top Hits Anime
              </Text>
              <Text style={{ color: "green" }}>see all</Text>
            </View>

            <FlatList
              horizontal={true}
              data={data1}
              renderItem={({ item }) => (
                <Link href={`/info/${item.id}`}>
                  <Image
                    source={{
                      uri: `${item.attributes.posterImage.tiny}`,
                    }}
                    style={{
                      width: 150,
                      height: 210,
                      marginRight: 15,
                      borderRadius: 10,
                    }}
                  />
                </Link>
              )}
              keyExtractor={(item) => item.id}
            />
          </View>
          <View style={{ paddingBottom: 20 }}>
            <View
              style={{
                flex: 1,
                flexDirection: "row",
                justifyContent: "space-between",
                paddingRight: 20,
              }}
            >
              <Text
                style={{ fontWeight: "bold", fontSize: 20, marginBottom: 20 }}
              >
                New Episode Releases
              </Text>
              <Text style={{ color: "green" }}>see all</Text>
            </View>

            <FlatList
              horizontal={true}
              data={data2}
              renderItem={({ item }) => (
                <Link href={`/info/${item.id}`}>
                  <Image
                    style={{
                      width: 150,
                      height: 210,
                      marginRight: 15,
                      borderRadius: 10,
                    }}
                    source={{
                      uri: `${item.attributes.posterImage.tiny}`,
                    }}
                  />
                </Link>
              )}
              keyExtractor={(item) => item.id}
            />
          </View>
        </View>
      </ScrollView>
      <View
        style={{
          position: "absolute",
          backgroundColor: "transparent",
          paddingTop: Constants.statusBarHeight + 10,
          paddingLeft: 25,
          flex: 1,
          justifyContent: "space-between",
          flexDirection: "row",
          width: 370,
          opacity: 30,
        }}
      >
        <Image
          style={{ width: 40, height: 40, zIndex: 3 }}
          source={require("../images/logo.png")}
        />
        <View
          style={{
            backgroundColor: "transparent",
            flexDirection: "row",
            gap: 15,
          }}
        >
          <AntDesign name="search1" size={32} color="#D6D6D6" />
          <AntDesign name="bells" size={32} color="#D6D6D6" />
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: "80%",
  },
});
